// Parse award-winning websites and prepare for crawling
const websites = [];
const sources = [
  // Awwwards nominees
  { url: 'https://www.awwwards.com/websites/nominees/', selector: '.site-link' },
  // CSS Design Awards
  { url: 'https://www.cssdesignawards.com/', selector: '.nominee-link' },
  // FWA
  { url: 'https://thefwa.com/', selector: '.site-url' },
  // Webby Awards
  { url: 'https://winners.webbyawards.com/', selector: '.winner-site' }
];

// For demo, use static list of exceptional sites
const exceptionalSites = [
  { url: 'https://stripe.com', tags: ['fintech', 'minimal', 'professional'] },
  { url: 'https://linear.app', tags: ['saas', 'productivity', 'dark-mode'] },
  { url: 'https://vercel.com', tags: ['developer', 'modern', 'gradient'] },
  { url: 'https://www.apple.com', tags: ['corporate', 'elegant', 'animation'] },
  { url: 'https://github.com', tags: ['developer', 'community', 'dark-mode'] },
  { url: 'https://www.figma.com', tags: ['design', 'collaborative', 'colorful'] },
  { url: 'https://notion.so', tags: ['productivity', 'clean', 'illustrations'] },
  { url: 'https://slack.com', tags: ['communication', 'playful', 'gradient'] },
  { url: 'https://www.shopify.com', tags: ['ecommerce', 'bold', 'conversion'] },
  { url: 'https://www.tesla.com', tags: ['automotive', 'futuristic', 'immersive'] }
];

// Filter sites not crawled in last 7 days
const now = new Date();
const sevenDaysAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

// Return sites to crawl
return exceptionalSites.map(site => ({
  json: {
    url: site.url,
    tags: site.tags,
    crawlOptions: {
      depth: 2,
      patterns: ['navigation', 'hero', 'card', 'form', 'button'],
      captureInteractions: true,
      viewports: ['desktop', 'tablet', 'mobile'],
      extractTokens: true
    }
  }
}));

// Process crawl results and extract insights
const crawlResult = items[0].json.data;
const insights = [];

// Analyze components
if (crawlResult.components && crawlResult.components.length > 0) {
  const componentsByType = {};
  crawlResult.components.forEach(component => {
    componentsByType[component.type] = (componentsByType[component.type] || 0) + 1;
  });

  insights.push({
    type: 'component_analysis',
    data: {
      total: crawlResult.components.length,
      byType: componentsByType,
      topPatterns: crawlResult.patterns?.map(p => p.name) || []
    }
  });
}

// Analyze design tokens
if (crawlResult.designTokens) {
  const { colors, typography, spacing } = crawlResult.designTokens;

  insights.push({
    type: 'design_tokens',
    data: {
      primaryColor: colors?.primary?.hex,
      colorCount: Object.keys(colors || {}).length,
      fontFamilies: typography?.fontFamilies?.map(f => f.family) || [],
      spacingBase: spacing?.base
    }
  });
}

// Quality scores
const qualityScores = {
  accessibility: crawlResult.components?.reduce((sum, c) => sum + (c.accessibilityScore || 0), 0) / (crawlResult.components?.length || 1),
  performance: crawlResult.metadata?.duration < 30000 ? 'fast' : 'slow',
  designQuality: crawlResult.patterns?.length > 3 ? 'high' : 'medium'
};

insights.push({
  type: 'quality_metrics',
  data: qualityScores
});

// Return processed data
return [{
  json: {
    website: crawlResult.website,
    insights,
    componentsFound: crawlResult.components?.length || 0,
    crawlDuration: crawlResult.metadata?.duration,
    timestamp: new Date().toISOString()
  }
}];

