// Generate weekly design digest
const allResults = items;
const digest = {
  week: new Date().toISOString().split('T')[0],
  totalSitesCrawled: allResults.length,
  totalComponentsFound: allResults.reduce((sum, item) => sum + item.json.componentsFound, 0),
  topPatterns: {},
  colorTrends: [],
  fontTrends: {}
};

// Aggregate patterns
allResults.forEach(result => {
  const patterns = result.json.insights.find(i => i.type === 'component_analysis')?.data.topPatterns || [];
  patterns.forEach(pattern => {
    digest.topPatterns[pattern] = (digest.topPatterns[pattern] || 0) + 1;
  });
});

// Aggregate colors
const colors = allResults.map(r => 
  r.json.insights.find(i => i.type === 'design_tokens')?.data.primaryColor
).filter(Boolean);
digest.colorTrends = [...new Set(colors)];

// Aggregate fonts
allResults.forEach(result => {
  const fonts = result.json.insights.find(i => i.type === 'design_tokens')?.data.fontFamilies || [];
  fonts.forEach(font => {
    digest.fontTrends[font] = (digest.fontTrends[font] || 0) + 1;
  });
});

return [{ json: digest }];
